import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './ChatBox.css';

function ChatBox() {
  const [input, setInput] = useState('');
  const [history, setHistory] = useState([]);
  const [isListening, setIsListening] = useState(false);
  const recognitionRef = useRef(null);

  // Load chat from localStorage on mount
  useEffect(() => {
    const stored = localStorage.getItem('chatHistory');
    if (stored) setHistory(JSON.parse(stored));
  }, []);

  // Persist chat to localStorage on change
  useEffect(() => {
    localStorage.setItem('chatHistory', JSON.stringify(history));
  }, [history]);

  const speakText = (text) => {
    const utterance = new SpeechSynthesisUtterance(text.replace(/[\u{1F600}-\u{1F64F}]/gu, ''));
    utterance.lang = 'en-US';
    window.speechSynthesis.speak(utterance);
  };

  const sendMessage = async (msg = input) => {
    if (!msg.trim()) return;

    const userMsg = {
      sender: 'user',
      text: msg,
      timestamp: new Date().toISOString(),
    };
    setHistory((prev) => [...prev, userMsg]);
    setInput('');

    try {
      const res = await axios.post('http://localhost:5000/chat', { message: msg });

      const botMsg = {
        sender: 'bot',
        text: res.data.response,
        correction: res.data.correction,
        feedback: res.data.feedback,
        timestamp: new Date().toISOString(),
      };
      setHistory((prev) => [...prev, botMsg]);
      speakText(botMsg.feedback);
    } catch (error) {
      console.error(error);
    }
  };

  const startListening = () => {
    if (!('webkitSpeechRecognition' in window)) {
      alert('Speech Recognition not supported');
      return;
    }

    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = 'en-US';
    recognition.onstart = () => setIsListening(true);
    recognition.onend = () => setIsListening(false);
    recognition.onerror = (e) => console.error(e);
    recognition.onresult = (e) => sendMessage(e.results[0][0].transcript);
    recognition.start();
    recognitionRef.current = recognition;
  };

  const formatTime = (iso) => new Date(iso).toLocaleTimeString([], {
    hour: '2-digit', minute: '2-digit'
  });

  const clearChat = () => {
    localStorage.removeItem('chatHistory');
    setHistory([]);
  };

  return (
    <div className="chatbox-container">
      <div className="chatbox-header">
        <h2>üó£Ô∏è Language Tutor</h2>
        <button onClick={clearChat}>üßπ Clear Chat</button>
      </div>

      <div className="chatbox-messages">
        {history.map((msg, idx) => (
          <div
            key={idx}
            className={`chat-message ${msg.sender === 'user' ? 'user' : 'bot'}`}
          >
            <div className="bubble">
              <p className="text">{msg.text}</p>
              {msg.correction && msg.sender === 'bot' && (
                <p className="correction"><strong>Correction:</strong> {msg.correction}</p>
              )}
              {msg.feedback && msg.sender === 'bot' && (
                <p className="feedback">{msg.feedback}</p>
              )}
              <span className="time">{formatTime(msg.timestamp)}</span>
            </div>
          </div>
        ))}
      </div>

      <div className="chatbox-input">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type or speak a sentence..."
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
        />
        <button onClick={() => sendMessage()}>Send</button>
        <button onClick={startListening} className={`mic-button ${isListening ? 'listening' : ''}`}>
          üé§ {isListening ? 'Listening...' : 'Speak'}
        </button>
      </div>
    </div>
  );
}

export default ChatBox;
